function pageItem(pageName,pageLocation){
	this.name= pageName; this.location= pageLocation;
}

var AppPages = [ 
	new pageItem('mobilescreen1', 'screen-E904.html'),
	new pageItem('current', 'screen-E904.html')
];

function  navigateTo(outcome ){ 
	for(i=0;i<AppPages.length;i++) {
		if( AppPages[i].name == outcome) {
        	window.location.href=AppPages[i].location;
      	}
    }
}


