 // prepare the form when the DOM is ready
$(document).ready(function() {

  // Setup the ajax indicator
  $('body.ui-mobile-viewport').append('<div id="ajaxBusy"><p><img src="res/lib/jquerymobile/images/ajax-loader.gif"></p></div>');

  $('#ajaxBusy').css({
    display:"none",
    margin:"0px",
    paddingLeft:"0px",
    paddingRight:"0px",
    paddingTop:"0px",
    paddingBottom:"0px",
    position:"absolute",
    width:"100%",
	height:"100%",
	zIndex:999999999,
	opacity:"0.5",
	backgroundColor:"#EEEEEE"
  });

  $('#ajaxBusy img').css({
    position: "absolute",
    left: "50%",
    top: "50%"
  })
});

// Ajax activity indicator bound to ajax start/stop document events
$(document).ajaxStart(function(){
  $('#ajaxBusy').show();
}).ajaxStop(function(){
  $('#ajaxBusy').hide();
});

function bindSpinner(){
	$(document).ajaxStart(showSpinner).ajaxStop(hideSpinner);
}

function unbindSpinner(){
	$(document).unbind("ajaxStart ajaxStop");	
}

function showSpinner(){
	$('#ajaxBusy').show();
}

function hideSpinner(){
	$('#ajaxBusy').hide();
}

function req()
   {
       var name_ = $("#j_4").attr("value");
       
       $(function() {
                $.ajax({
                          type: "GET",
                          url: "http://orven.kodingen.com/name.php",
                          data: ({name: name_}),
                          cache: false,
                          dataType: "text",
                          success: onSuccess
                    });

              function onSuccess(data)
               {
                $("#j_6").html(data);
               }
          });
 
  }
